<script>
  import { web3Accounts, web3Enable } from "@polkadot/extension-dapp";
  import { ApiPromise, WsProvider } from "@polkadot/api";
  import { getContext } from "svelte";
  import Modal from "../Modal.svelte";

  let context = getContext("global");
  let selected = context.multistep;

  context.multistep.subscribe((n) => (selected = n));

  const connect = async () => {
    const extensions = await web3Enable("Kusama Tips Widget");
    const provider = new WsProvider("wss://westend-rpc.polkadot.io/");

    if (extensions.length === 0) {
      alert("Please install Polkadot extension!");
      context.multistep.reset();
      return;
    }
    context.accounts.set(await web3Accounts());
    context.multistep.nextStep();

    let api = new ApiPromise({ provider });
    await api.isReady;

    let [chain, nodeName, nodeVersion] = await Promise.all([
      api.rpc.system.chain(),
      api.rpc.system.name(),
      api.rpc.system.version(),
    ]);

    console.log(
      `Connected to chain ${chain} using ${nodeName} v${nodeVersion}`
    );
  };
</script>

<Modal index={0} on:close {selected}>
  <h2 slot="header" class="text-lg text-dark">Connect to Polkadot</h2>

  <div slot="content">
    <div class="bg-white rounded shadow p-8 mb-8">
      <p class="text-sm text-medium mb-4">
        In order to use a Tip or Donate button you must have Polkadot Wallet
        installed and funded with KSM tokens.
      </p>
      <div class="text-base text-medium">
        <p class="py-2 font-semibold">Extensions:</p>
        <div class="flex justify-evenly">
         
            <span class="text-sm text-center">On Google Chrome, install via 
              <a
              href="https://chrome.google.com/webstore/detail/polkadot%7Bjs%7D-extension/mopnmbcafieddcagagdcbnhejhlodfdd"
              target=" _blank"
              rel="noopener noreferrer"
              class="underline">
              <img
                class="w-10 h-10 mt-4 mx-auto"
                src="data:image/svg+xml;base64,"
                alt="" />
            </a>
            </span> 
          
         
            <span class="text-sm text-center">On Firefox, install via
            <a
              href="https://addons.mozilla.org/en-US/firefox/addon/polkadot-js-extension/"
              target=" _blank"
              rel="noopener noreferrer"
              class="underline">
              <img
                class="w-10 h-10 mt-4 mx-auto"
                src="data:image/svg+xml;base64,"
              alt="" />
            </a>

            </span>  
          
        </div>
      </div>
    </div>
    <button
      on:click={connect}
      class="block bg-accent py-2 px-6 text-white font-semibold rounded mx-auto
      hover:bg-dark">
      Connect
    </button>
  </div>
</Modal>
